import{_ as a,c as l,o,af as t,ag as i}from"./chunks/framework.CMOex9hH.js";const _=JSON.parse('{"title":"Модели","description":"","frontmatter":{"outline":"deep"},"headers":[],"relativePath":"models/index.md","filePath":"models/index.md"}'),r={name:"models/index.md"};function n(s,e,d,p,c,h){return o(),l("div",null,[...e[0]||(e[0]=[t('<h1 id="модели" tabindex="-1">Модели <a class="header-anchor" href="#модели" aria-label="Permalink to “Модели”">​</a></h1><p><strong>Модели</strong> — это все объекты, которые находятся на сцене и участвуют в показе вашего проекта.</p><h2 id="создание-моделеи" tabindex="-1">Создание моделей <a class="header-anchor" href="#создание-моделеи" aria-label="Permalink to “Создание моделей”">​</a></h2><p>Модели могут быть созданы двумя способами:</p><ol><li><strong>Добавление выбранного типа модели</strong> через интерфейс:</li></ol><p><img src="'+i+'" alt="Форма создания модели" style="display:block;margin:auto;"></p><blockquote><p>Обращаем внимание - через панель добавления модель добавится как дочерняя в текущую выбранную модель, или в корень сцены, если нет выбранной модели.</p></blockquote><ol start="2"><li><strong>Перетаскивание ресурсов</strong> на панель моделей — это автоматически создаст модель из подходящего ресурса.</li></ol><p>При выборе модели в списке моделей в <strong>Инспекторе</strong> появятся формы для редактирования параметров модели, таких как:</p><ul><li>Положение на сцене</li><li>Размер</li><li>Название</li><li>Другие характеристики</li></ul><h2 id="параметры-моделеи" tabindex="-1">Параметры моделей <a class="header-anchor" href="#параметры-моделеи" aria-label="Permalink to “Параметры моделей”">​</a></h2><p>Параметры моделей работают в двух режимах:</p><ul><li><strong>Глобальные параметры</strong> — меняют состояние модели во всех слайдах</li><li><strong>Локальные параметры</strong> — меняют модель только в текущем выбранном слайде</li></ul><blockquote><p><strong>Пример</strong>: Если вы измените положение модели в слайде 1, а затем в слайде 2 установите другое положение — при переключении между слайдами модель будет автоматически перемещаться между этими позициями.</p></blockquote><blockquote><p><strong>Важно</strong>: При удалении модели она удаляется из всех слайдов. Если нужно скрыть модель только в определенном слайде — отключите ее видимость в этом слайде.</p></blockquote><h3 id="другие-изменяемые-параметры-могут-включать" tabindex="-1">Другие изменяемые параметры могут включать: <a class="header-anchor" href="#другие-изменяемые-параметры-могут-включать" aria-label="Permalink to “Другие изменяемые параметры могут включать:”">​</a></h3><ul><li>Цвет модели</li><li>Интенсивность света для источников освещения</li><li>Размер шрифта для текстовых моделей</li><li>И многие другие характеристики</li></ul><h2 id="расширение-списка-моделеи" tabindex="-1">Расширение списка моделей <a class="header-anchor" href="#расширение-списка-моделеи" aria-label="Permalink to “Расширение списка моделей”">​</a></h2><p><em>Для разработчиков:</em> При добавлении новых типов моделей через модули необходимо указывать, какие формы (кроме стандартных) нужно отображать. Подробнее в руководстве для разработчиков.</p><blockquote><p><em>Справочный материал этого раздела находится в процессе подготовки.</em></p></blockquote>',20)])])}const u=a(r,[["render",n]]);export{_ as __pageData,u as default};
